mapper.select:
SELECT b.no, b.contents, b.title, b.ins_timestamp, b.upd_timestamp, c.name as category_name, m.member_id as member_id, m.name as member_name FROM t_blog b LEFT OUTER JOIN t_blog_category c ON b.category_no = c.no INNER JOIN t_member m ON b.member_no = m.no WHERE
1 = 1
AND c.no = #{category_no}
ORDER BY b.no DESC LIMIT #{page_content_size} OFFSET #{start_index}

mapper.update:

UPDATE t_blog
SET
contents = #{contents}
WHERE no = #{no}
